<script lang="ts" setup>
import {
  AlButton,
  AlCheckbox,
  AlIcon,
  AlInput,
  Hide,
  Lock,
  SuccessFilled,
  User,
  View,
} from '@ai-lowcode/element-plus'

import { AlHttp } from '@ai-lowcode/request'
import { onMounted, ref } from 'vue'

import loginBg from '@/assets/img/login-bg.png'
import { useUserStore } from '@/store/modules/user'

const bg = 'data:image/png;base64,'

const loginForm = ref({
  username: '',
  password: '',
  verifyCode: '',
})

const verificationCode = ref({
  id: '',
  img: '',
})

const showPassword = ref(false)

const loginLoading = ref(false)

const userStore = useUserStore()

function changePasswordStatus() {
  showPassword.value = !showPassword.value
}

async function handleLogin() {
  loginLoading.value = true
  await userStore.login({
    ...loginForm.value,
    captchaId: verificationCode.value.id,
  })
  loginLoading.value = false
}

async function handleVerificationCode() {
  const { data } = await AlHttp.get('/auth/captcha/img', {
    width: 100,
    height: 50,
  }, {
    isShowSuccessMessage: false,
    isShowErrorMessage: false,
  })
  verificationCode.value = data
}

onMounted(() => {
  handleVerificationCode()
})
</script>

<template>
  <div class="flex justify-center items-center h-full" :style="`background: #fff url(${bg})`">
    <div class="w-[900px] h-[480px] bg-basic-color rounded-md flex" style="box-shadow: 0 1px 14px 2px rgb(7 27 48 / 9%)">
      <img class="w-[420px] h-full bg-cover p-[20px]" :src="loginBg" alt="登录背景">
      <div class="p-[60px] flex-1">
        <div class="text-xl text-center">
          欢迎登录 Ai Lowcode
        </div>
        <div class="mt-[40px]">
          <AlInput v-model="loginForm.username" :prefix-icon="User" placeholder="请输入账户" />
          <AlInput v-model="loginForm.password" :prefix-icon="Lock" :type="showPassword ? 'text' : 'password'" class="mt-[20px]" placeholder="请输入密码">
            <template #suffix>
              <AlIcon class="cursor-pointer" @click="changePasswordStatus">
                <View v-if="showPassword" />
                <Hide v-else />
              </AlIcon>
            </template>
          </AlInput>
          <AlInput v-model="loginForm.verifyCode" :prefix-icon="SuccessFilled" class="mt-[20px]" placeholder="请输入验证码">
            <template #suffix>
              <img :src="verificationCode.img" alt="验证码" class="cursor-pointer h-[30px]" @click="handleVerificationCode">
            </template>
          </AlInput>
          <div class="flex justify-between items-center mt-[15px]">
            <AlCheckbox>记住密码</AlCheckbox>
            <div class="text-sm cursor-pointer text-blue-600 select-none">
              忘记密码
            </div>
          </div>
          <div class="mt-[20px]">
            <AlButton :loading="loginLoading" class="w-full" type="primary" @click="handleLogin">
              登录
            </AlButton>
          </div>
          <div class="mt-[15px]">
            <AlButton class="w-full" text>
              注册账户
            </AlButton>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>
